mapper.select:
select *
from user
where userName = #{userName}
  and password = #{password}

select * from user

    roleId='4'

        and name=#{name}



        and className=#{className}



        and college=#{college}



        and userName=#{userName}



order by id desc
limit #{page},#{pageSize};

mapper.update:
update user
set warningStatus=0,
    warningReason=#{warningReason}
where userName = #{userName}
  and name = #{name}

update user
set warningStatus=1,
    warningReason=''
where userName = #{userName}
  and name = #{name}
